mapper.select:
SELECT a.*
FROM t_admin a
WHERE a.seq = #{seq}

mapper.insert:
/** ManageMapper.admin_menu_first_save */
INSERT INTO t_first_menu(
         keyword
        ,type
        ,title
        ,url
        ,sort
        ,popupYn
        ,stat
        ,insertDate
        ,writer
)
VALUES (#{keyword}
        ,#{type}
        ,#{title}
        ,#{url}
        ,#{sort}
        ,#{popupYn}
        ,#{stat}
        ,now()
        ,#{writer})

